import React from "react";
import "../style/products.css";

const Produtos = (props) => {
  const [products, setProducts] = React.useState([]);

  React.useEffect(() => {
    async function catchProdutcs() {
      const response = await fetch("http://localhost/PHP_to_React/db.php");
      const result = await response.json();
      console.log(result);
      setProducts(result);
    }
    catchProdutcs();
  }, []);

  return (
    <div>
      {products &&
        products.map(
          ({
            categoria,
            descricao,
            preco_antigo,
            preco_novo,
            imagem,
            alt_imagem,
          }) => (
            <div className="productCards">
              <img className="image" src={imagem} alt={alt_imagem} />
              <h3>{descricao}</h3>
              <p className="oldPrice">R$ {preco_antigo}</p>
          <p className="newPrice">R$ {}</p>
              <button>
                <i className="fas fa-shopping-basket"></i>Comprar
              </button>
            </div>
          )
        )}
    </div>
  );
};

export default Produtos;
