  const products = useSelector(state => state.products)
  const offerProduct = products[0]
  console.log(offerProduct.descricao);

  return (
    <main>
      <h2 className="welcomeTitle">
        Bem vindo, a <span>Eletro Dev Store!</span>{" "}
      </h2>

      <section className="offerSection">
              <div className="offerCards">
                <img className="image" src={offerProduct.imagem} alt={offerProduct.alt_imagem}/>
                <h3>{offerProduct.descricao}</h3>
                <p className="oldPrice">{preco_antigo.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}</p>
                <p className="newPrice">{preco_novo.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}</p>
                <button>
                  <i className="fas fa-shopping-basket"></i>Comprar
                </button>
              </div>
            )
      </section>
    </main>
  );
};

export default Home;
