import products from '../../api';

const initialState = [...products];

const productsReducer = (state = initialState, action) => {
  switch(action.type){
    case 'CATEGORY_FILTER':
      return state.map(product => {
        product.ativo === true
        return product;
      });

    default:
      return state;
  }
} 

export default productsReducer;
