import React from "react";
import { useSelector } from 'react-redux';

const Home = () => {
  const products = useSelector(state => state.products)

  return (
    <main>
      <h2 className="welcomeTitle">
        Bem vindo, a <span>Eletro Dev Store!</span>{" "}
      </h2>

      <section className="offerSection">
        {products[0] &&
          products.map(
            ({ id_produto, descricao, preco_antigo, preco_novo, imagem, alt_imagem }) => (
              <div key={id_produto} className="offerCards">
                <img className="image" src={imagem} alt={alt_imagem}/>
                <h3>{descricao}</h3>
                <p className="oldPrice">{preco_antigo.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}</p>
                <p className="newPrice">{preco_novo.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}</p>
                <button>
                  <i className="fas fa-shopping-basket"></i>Comprar
                </button>
              </div>
            )
          )}
      </section>
    </main>
  );
};

export default Home;
