import React from "react";
import Categories from "../components/Categories";
import "../style/products.css";
import { useSelector, useDispatch } from "react-redux";

const Produtos = () => {
  const products = useSelector((state) => state.productsReducer);
  const dispatch = useDispatch();

  return (
    <div className="productContainer">
      <Categories />

      <section className="productSection">
        {products &&
          products.map(
            ({produto}) => (
              <div key={produto.id_produto} className="productCards">
                <img className="image" src={produto.imagem} alt={produto.alt_imagem} />
                <h3>{produto.descricao}</h3>
                <p className="oldPrice">
                  {produto.preco_antigo.toLocaleString("pt-BR", {
                    style: "currency",
                    currency: "BRL",
                  })}
                </p>
                <p className="newPrice">
                  {produto.preco_novo.toLocaleString("pt-BR", {
                    style: "currency",
                    currency: "BRL",
                  })}
                </p>
                <button>
                  <i
                    className="fas fa-shopping-basket"
                    onClick={() =>
                      dispatch({ type: "ADD_TO_CART", id_produto: produto.id_produto })
                    }
                  ></i>
                  Comprar
                </button>
              </div>
            )
          )}
      </section>
    </div>
  );
};

export default Produtos;
