import products from '../../api';

const initialState = products.map(product => ({...product}));

const categoryReducer = (state = initialState, action) => {
  switch(action.type) {
    case 'CATEGORY_FILTER':
      // return state.map((product) => {
      //   if(product.categoria === action.categoria)
      //     return product;
        return state;
      // });

    default:
      return state;
  }
}

export default categoryReducer;
