import React from "react";
import "../style/modal.css";

const Modal = () => {
  function callModal(modalID) {
    const modal = document.getElementById(modalID);

    if (modal) {
      modal.classList.add("display");

      modal.addEventListener("click", (action) => {
        if (
          action.target.id === modalID ||
          action.target.classList === "close"
        ) {
          modal.classList.remove("display");
        }
      });
    }
  }

  let image = document.querySelector(".image");
  let modalImage = document.getElementById("imageContainer");

  function showIn(el) {
    callModal("modalContainer");
    let imageSource = el.src;
    modalImage.src = imageSource;
  }

  return (
    <div id="modalContainer" className="modalContainer">
      <div className="modal">
        <button>
          <span className="close">&times;</span>
        </button>
        <img className="modalContent" id="imageContainer" alt="" />
      </div>
    </div>
  );
};

export default Modal;
