      const result = await response.json();
      setProducts(result);
    }
    catchProducts();
  }, []);

  return (
<div class="categories">
                <h3>Categorias</h3>
                <button class="categoriesButtons">Todos</button>
                <button class="categoriesButtons">Vídeogames</button>
                <button class="categoriesButtons">Geladeiras</button>
                <button class="categoriesButtons">Celulares</button>
                <button class="categoriesButtons">Lavadoras</button>
                <button class="categoriesButtons">Informática</button>
                <button class="categoriesButtons">Tablets</button>
                <button class="categoriesButtons">Televisores</button>
            </div>
  )
}

