import React from "react";
import { useSelector } from 'react-redux';
import '../style/cart.css'

const Cart = () => {
  const deactivatingCart = (e) => {
    const cartContainer = document.querySelector('.cartContainer').className
    const removeClass = cartContainer === 'cartContainer showing' ? 'cartContainer' : 'cartContainer showing'
    if (e.target.classList.value === 'cartContainer showing') {
      document.querySelector('.cartContainer').className = removeClass;
    }
  }

  const cart = useSelector((state) => state.cartReducer)

  return (
    <aside className='cartContainer' id="cartContainer" onClick={deactivatingCart}>
      <div className="cart">
        <h3>Seu carrinho de compras</h3>
        <div className="items">
          <div className="item">
            <p>
              <span>Sony Playstation 5 </span>| Qnt: <span>1</span>
            </p>
            <p>
              Valor: R$ <span>4.499,00</span>
            </p>
          </div>

          <div className="buttons">
            <button className="cartButton clean">
              Limpar
            </button>
            <button
              className="cartButton  checkout"
              onClick={() => alert('Em breve')}
            >
              Comprar
            </button>
          </div>
        </div>
      </div>
    </aside>
  );
};

export default Cart;
