import React from "react";
import Produtos from '../pages/Produtos'

const Categories = () => {
  const [filter, setFilter] = React.useState("all");
  const [products, setProducts] = React.useState([]);

  React.useEffect(() => {
    setProducts(products);
  }, []);

  React.useEffect(() => {
    setProducts([]);

    const filtered = products.map(p => ({
      ...p,
      filtered: p.category.includes(filter)
    }));
    setProducts(filtered);
  }, [filter]);

  return (
    <div className="categories">
      <h3>Categorias</h3>
      <button className="categoriesButtons" active={filter === "all"} onClick={() => setFilter("all")}>Todos</button>
      <button className="categoriesButtons">Vídeogames</button>
      <button className="categoriesButtons">Geladeiras</button>
      <button className="categoriesButtons">Celulares</button>
      <button className="categoriesButtons">Lavadoras</button>
      <button className="categoriesButtons">Informática</button>
      <button className="categoriesButtons">Tablets</button>
      <button className="categoriesButtons">Televisores</button>
    </div>
  );
};

export default Categories;
