import React from "react";

const Messages = () => {
  const [messages, setMessages] = React.useState([]);

  React.useEffect(() => {
    async function catchMessages() {
      const response = await fetch("http://localhost/PHP_to_React/db.php");
      const result = await response.json();
      setMessages(result);
    }
    catchMessages();
  }, []);

  return (
    <>
      <h2>Mensagens</h2>
      {messages &&
        messages.map(({ id_usuario, nome, email, msg, data }) => (
          <div key={id_usuario} className="messages">
            <p>
              {nome} | {email}
            </p>
            <p>{msg}</p>
            <p>{data}</p>
          </div>
        ))}
    </>
  );
};

export default Messages;
